    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
        String rut;
        char tipo = ' ';
        boolean ahorro, corriente;
        String tipox;
        int saldo;
        
        DefaultTableModel tabla = (DefaultTableModel) tblDatos.getModel();
        
        rut = txtRut.getText();
        ahorro = chkAhorro.isSelected();
        corriente = chkCorriente.isSelected();
        if(ahorro==true && corriente==false){
            tipo = 'A';
        }
        else if(ahorro==false && corriente==true){
            tipo = 'C';
        }
        
        System.out.println(tipo);
        //Limpiar
        tabla.setRowCount(0);
        
        //Buscar por rut y tipo
        if(rut.isEmpty()==false && (tipo=='A'||tipo=='C')){
            List<Cliente> listaRutTipo = RegistroCliente.getLista(rut, tipo);
        
            for (Cliente tmp : listaRutTipo) {
                rut = tmp.getRut();
                tipo = tmp.getCuenta().getTipoCuenta();
                if (tmp.getCuenta().getTipoCuenta() == 'A') {
                    tipox = "Ahorro";
                }
                else{
                    tipox = "Corriente";
                }
                saldo = tmp.getCuenta().getSaldo();
                tabla.addRow(new Object[]{rut, tipox, saldo});
            }
        }
        
        //Buscar solo por rut (ambos o ningun tipo seleccionados)
        else if(rut.isEmpty()==false && tipo == ' '){
            List<Cliente> listaRut = RegistroCliente.getLista(rut);
            
            for (Cliente tmp : listaRut) {
                rut = tmp.getRut();
                tipo = tmp.getCuenta().getTipoCuenta();
                if (tmp.getCuenta().getTipoCuenta() == 'A') {
                    tipox = "Ahorro";
                }
                else{
                    tipox = "Corriente";
                }
                saldo = tmp.getCuenta().getSaldo();
                tabla.addRow(new Object[]{rut, tipox, saldo});
            }
        }
        
        //Si el rut est√° vacio y ambos tipos
        else if(rut.isEmpty()&&tipo==' '){
            List<Cliente> listaTipo = RegistroCliente.getLista();
            for (Cliente tmp : listaTipo) {
                rut = tmp.getRut();
                tipo = tmp.getCuenta().getTipoCuenta();
                if (tmp.getCuenta().getTipoCuenta() == 'A') {
                    tipox = "Ahorro";
                }
                else{
                    tipox = "Corriente";
                }
                saldo = tmp.getCuenta().getSaldo();
                tabla.addRow(new Object[]{rut, tipox, saldo});
            }
        }
        //Si el rut vacio y hay un tipo marcado
        else{
            List<Cliente> listaTipo = RegistroCliente.getLista(tipo);
            for (Cliente tmp : listaTipo) {
                rut = tmp.getRut();
                tipo = tmp.getCuenta().getTipoCuenta();
                if (tmp.getCuenta().getTipoCuenta() == 'A') {
                    tipox = "Ahorro";
                }
                else{
                    tipox = "Corriente";
                }
                saldo = tmp.getCuenta().getSaldo();
                tabla.addRow(new Object[]{rut, tipox, saldo});
            }
        
        }
        
    }//GEN-LAST:event_btnBuscarActionPerformed

